"use client";

import Image from "next/image";
import React, { useState } from "react";

function Projects_filtered() {
    const products = [
        { id: 2, image: "data:image/jpeg;base64,", price: "78000", name: "Ipad pro", category: "electronics" },
        { id: 3, image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRgr-5EvtNirLZcA3_4-xS_4-Uw1t4okrJW8g&s", price: "2500", name: "Jordan 1", category: "shoes" },
        { id: 4, image: "data:image/jpeg;base64,", price: "1500", name: "Jeans", category: "shoes" },
        { id: 5, image: "data:image/jpeg;base64,", price: "1600", name: "Trousers", category: "cloths" },
        { id: 6, image: "data:image/jpeg;base64,", price: "250", name: "Denim jacket", category: "cloths" },
        { id: 7, image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQWgz6N-XbEHJFBijmUvjW4niXI4FbuB6uPQA&s", price: "350000", name: "Iphone 16Pro", category: "electronics" },
        { id: 8, image: "data:image/jpeg;base64,", price: "350500", name: "Sumsung S24", category: "electronics" },
    ];

    const [selectedCategory, setSelectedCategory] = useState("All");

    const uniqueCategories = ["All", ...new Set(products.map((product) => product.category))];

    const filteredProducts = products.filter((product) =>
        selectedCategory === "All" ? true : product.category === selectedCategory
    );

    return (
        <div className="p-6">
            {/* Category Buttons */}
            <div className="flex flex-wrap gap-4 mb-6">
                {uniqueCategories.map((category, index) => (
                    <button
                        key={index}
                        onClick={() => setSelectedCategory(category)}
                        className={`px-4 py-2  ${
                            selectedCategory === category
                                ? "border-t-0 border-l-0 border-r-0 border-b-2 border-y-blue-700 text-white"
                                : "bg-transparent border-t-0 border-l-0 border-r-0 border-b-2 text-gray-800"
                        } hover:border-y-blue-700 hover:text-white  transition-all ease-in-out duration-[200ms]`}
                    >
                        {category}
                    </button>
                ))}
            </div>

            {/* Filtered Products */}
            <div className="grid sm:grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {filteredProducts.length > 0 ? (
                    filteredProducts.map((product) => (
                        <div key={product.id} className="border p-4 rounded shadow flex flex-col items-center">
                            <Image
                                src={product.image}
                                alt={product.name}
                                width={150}
                                height={150}
                                className="rounded"
                            />
                            <h2 className="text-lg font-semibold mt-2">{product.name}</h2>
                            <p className="text-gray-700">Price: Ksh {product.price}</p>
                        </div>
                    ))
                ) : (
                    <p className="col-span-2 text-center">No products found in this category.</p>
                )}
            </div>
        </div>
    );
}

export default Projects_filtered;
